const date2 = $input.first().json.Orientation__orientation_date;

// Get today's date as the start date
const date1 = new Date().toISOString();

function calculateDateDifference(date1, date2) {
    const startDate = new Date(date1);
    const endDate = new Date(date2);

    // Calculate the difference in milliseconds
    const differenceInMilliseconds = Math.abs(endDate - startDate);

    // Convert to desired units
    const differenceInDays = Math.floor(differenceInMilliseconds / (1000 * 60 * 60 * 24));
    const differenceInHours = Math.floor(differenceInMilliseconds / (1000 * 60 * 60)) % 24;
    const differenceInMinutes = Math.floor(differenceInMilliseconds / (1000 * 60)) % 60;
    const differenceInSeconds = Math.floor(differenceInMilliseconds / 1000) % 60;

    return {
        days: differenceInDays,
        hours: differenceInHours,
        minutes: differenceInMinutes,
        seconds: differenceInSeconds,
    };
}

// Call the function and format the result as an array of objects
const difference = calculateDateDifference(date1, date2);

// Return as an array of objects for n8n
return [
    {
        json: difference,
    },
];
