// Convert 'created_at' to epoch in microseconds
const receipt_date = $('Split In Batches1').item.json.receipt_date;
const deposit_date = $('Split In Batches1').item.json.deposit_date;
const created_at = $('Split In Batches1').item.json.created_at;

const ReceiptDate = new Date(receipt_date);
const DepositDate = new Date(deposit_date);
const CreatedAt = new Date(created_at);

const epochMicrosecondsReceipt = ReceiptDate.getTime() * 1000;
const epochMicrosecondsDepost = DepositDate.getTime()*1000;// Convert milliseconds to microseconds
const epochCreatedAt = CreatedAt.getTime()*1000;

return {
    receipt_date_epoch_microseconds: epochMicrosecondsReceipt,
  depost_date_epoch_microseconds:
    epochMicrosecondsDepost,
  created_at_epoch_microseconds:
epochCreatedAt

