// Define the mapping of digits to Unicode characters
const unicodeMap = {
    0: '\u00A0',  // U+00A0
    1: '\u2000',  // U+2000
    2: '\u2002',  // U+2002
    3: '\u2004',  // U+2004
    4: '\u2005',  // U+2005
    5: '\u2006',  // U+2006
    6: '\u2009',  // U+2009
    7: '\u200A'   // U+200A
};

// Combine all Unicode characters into a single regular expression
const unicodePattern = '[\u00A0\u2000\u2002\u2004\u2005\u2006\u2009\u200A]+';

// Function to extract the custom base-8 Unicode ID
function extractInvisibleId(text) {
    // Regular expression to match a sequence of custom base-8 Unicode characters
    const pattern = new RegExp(`(${unicodePattern})`);
    const match = text.match(pattern);

    if (match) {
        return match[0];  // Return the entire matched Unicode sequence as the ID
    } else {
        return null;
    }
}

// Process input items
for (const item of $input.all()) {
    // Extract the input string from the JSON structure
    const inputString = $('Webhook').first().json.body.entry[0].changes[0].value.messages[0].text.body;

    // Count occurrences of zero-width spaces (U+200B)
    const countZeroWidthSpaces = (inputString.match(/\u200B/g) || []).length;

    // Add the count to the JSON of each item
    item.json.zeroWidthSpaceCount = countZeroWidthSpaces;

    // Extract the invisible ID (if present)
    const invId = extractInvisibleId(inputString);
    item.json.inv_id = invId;

    if (invId) {
        // Remove the extracted Unicode ID from the text
        const updatedString = inputString.replace(invId, '').trim();
      $('Webhook').first().json.body.entry[0].changes[0].value.messages[0].text.body = updatedString;
    }
}

return $input.all();

