function isValidText(input) {
    // Convert input to lowercase
    const lowerCaseInput = input.toLowerCase();

    // Define allowed and blocked keywords
    const allowedKeywords = ["miles team", "team miles", "know more","interested","learn","cpa","cma","us pathway","miles us pathway","ea","hi miles team","hey miles team","hi"];
    const blockedKeywords = ["spam", "stop", "do not send","unnecessary","chup","shut up"];  // Add other terms as needed

    // Define regex patterns to match unwanted structures (optional)
    const blockedPatterns = [
        /https?:\/\/[^\s]+/,  // URLs
        /\b(?:free|offer|win|guarantee|cheap)\b/  // Add other unwanted patterns here
    ];

    // Check for blocked keywords
    const hasBlockedKeyword = blockedKeywords.some(keyword => lowerCaseInput.includes(keyword));

    // Check for allowed keywords
    const hasAllowedKeyword = allowedKeywords.some(keyword => lowerCaseInput.includes(keyword));

    // Check for blocked patterns
    const hasBlockedPattern = blockedPatterns.some(pattern => pattern.test(lowerCaseInput));

    // Determine if text is valid based on conditions
    const isValid = hasAllowedKeyword && !hasBlockedKeyword && !hasBlockedPattern;

    // Return the result as an array of objects, each with a JSON key (n8n format)
    return [{ json: { input, isValid } }];
}

// Example usage in n8n format:
