const url = $('Postgres2').json.location_url;

const regex = /@([-+]?[0-9]*\.?[0-9]+),([-+]?[0-9]*\.?[0-9]+)/;
const match = url.match(regex);

if (match) {
  const coordinates = [match[1], match[2]]; // Latitude is match[1], Longitude is match[2]
  console.log("Coordinates:", coordinates);
} else {
  console.log("Coordinates not found");
}

