// Loop over input items and add a new field called 'myNewField' to the JSON of each one
for (const item of $input.all()) {
if ($input.first().json.course_details.id == 3 && ($input.first().json.lead_details.education_qualification != 'completed_graduation')) {
  item.json.m102 = [item.json?.course_details.wa_content.filter(e=>e.id == 'm102')[0]];
  item.json.m103 = [item.json?.course_details.wa_content.filter(e=>e.id == 'm103')[0]];
}else if($input.first().json.course_details.id == 3 && ($input.first().json.lead_details.education_qualification == 'completed_graduation')){
  item.json.m102 = [item.json?.course_details.wa_content.filter(e=>e.id == 'm102')[1]];
  item.json.m103 = [item.json?.course_details.wa_content.filter(e=>e.id == 'm103')[1]];
} else {
  item.json.m102 = item.json?.course_details.wa_content.filter(e=>e.id == 'm102');
  item.json.m103 = item.json?.course_details.wa_content.filter(e=>e.id == 'm103');
}
}

return $input.all();
